import * as core from '@actions/core';
import * as github from '@actions/github';
import { validateAndGetConfig } from './src/config/index.js';
import { GitHubClient } from './src/github/api-client.js';
import { GeminiClient } from './src/gemini/api-client.js';
import { IssueAnalyzer } from './src/gemini/analyzer.js';
import { BranchManager } from './src/github/branch-manager.js';
import { WorkflowManager } from './src/workflow/manager.js';

async function run() {
  let config;
  const CENTRAL_REPO = 'alphaleadership/kanbanaction';

  try {
    config = validateAndGetConfig();

    if (config.debug) {
        console.log('Debug mode enabled');
        console.log('Context:', JSON.stringify(github.context, null, 2));
    }

    const githubClient = new GitHubClient(config.githubToken, config.githubRepo);

    if (config.installWorkflows) {
      const workflowInstaller = new WorkflowManager(githubClient, null, null, null);
      await workflowInstaller.installWorkflows();
      console.log('Workflow installation completed. Skipping event processing.');
      return;
    }

    const geminiClient = new GeminiClient(config.geminiApiKey, {
      primaryModel: config.geminiModel,
      fallbackModels: config.geminiFallbackModels,
      retries: config.geminiRetries
    });
    const analyzer = new IssueAnalyzer(geminiClient);
    const branchManager = new BranchManager(githubClient);
    const workflow = new WorkflowManager(githubClient, geminiClient, analyzer, branchManager);

    const context = github.context;

    if (context.eventName === 'issues' && (context.payload.action === 'opened' || context.payload.action === 'labeled')) {
      const issueNumber = context.payload.issue.number;
      await workflow.processGitHubIssue(issueNumber);
    } else if (context.eventName === 'pull_request' && context.payload.action === 'closed' && context.payload.pull_request.merged) {
      const prNumber = context.payload.pull_request.number;
      await workflow.processPullRequestMerge(prNumber);
    } else if (context.eventName === 'workflow_dispatch' || context.eventName === 'schedule') {
      console.log(`Triggered by ${context.eventName}. Processing pending tasks...`);
      await workflow.processPendingTasks();
    } else {
      console.log(`Event ${context.eventName} with action ${context.payload.action} is not handled.`);
    }

      } catch (error) {
        core.setFailed(error.message);
        if (config?.debug) {
          console.error('Detailed Error:', error);
          console.error('Error Stack:', error.stack);
        }
    if (!config?.githubToken) {
      console.error('Skipping centralized error report because no GitHub token is configured.');
      return;
    }

    try {
      console.log(`Reporting error to central repository: ${CENTRAL_REPO}`);
      const centralClient = new GitHubClient(config.githubToken, CENTRAL_REPO);
      const errorTitle = `Error in ${github.context.payload.repository?.full_name || 'unknown repo'}: ${error.message.substring(0, 50)}...`;
      const errorBody = `
### Error Details
- **Repository:** ${github.context.payload.repository?.full_name || 'Unknown'}
- **Event:** ${github.context.eventName}
- **Action:** ${github.context.payload.action || 'N/A'}
- **Error Message:** ${error.message}

### Stack Trace
\`\`\`
${error.stack}
\`\`\`

### Context
<details>
<summary>GitHub Context</summary>

\`\`\`json
${JSON.stringify(github.context, null, 2)}
\`\`\`
</details>
      `;

      await centralClient.createIssue(errorTitle, errorBody, ['bug', 'automated-report']);
    } catch (reportError) {
      console.error('Failed to report error to central repository:', reportError.message);
    }
  }
}

run();

import * as core from '@actions/core';

export function getConfig(env = process.env) {
  const configuredFallbackModels = core.getInput('gemini-fallback-models') || env.GEMINI_FALLBACK_MODELS || '';
  const parsedRetries = Number.parseInt(core.getInput('gemini-retries') || env.GEMINI_RETRIES || '3', 10);

  const config = {
    geminiApiKey: env.INPUT_GEMINI_API_KEY || core.getInput('gemini-api-key') || env.GEMINI_API_KEY,
    geminiModel: core.getInput('gemini-model') || env.GEMINI_MODEL || 'gemini-2.5-flash',
    geminiFallbackModels: configuredFallbackModels
      .split(',')
      .map(model => model.trim())
      .filter(Boolean),
    geminiRetries: Number.isNaN(parsedRetries) || parsedRetries < 0 ? 3 : parsedRetries,
    githubToken: env.INPUT_GITHUB_TOKEN || core.getInput('github-token') || env.GITHUB_TOKEN,
    installWorkflows: core.getInput('install-workflows') === 'true' || env.INSTALL_WORKFLOWS === 'true'|| env.INPUT_INSTALL_WORKFLOWS === 'true',
    debug: core.getInput('debug') === 'true' || env.DEBUG === 'true',
    githubRepo: env.GITHUB_REPOSITORY,
    githubRef: env.GITHUB_REF
  };

  return config;
}

export function validateAndGetConfig(env = process.env) {
    const config = getConfig(env);

    const errors = [];
    if (!config.installWorkflows && !config.geminiApiKey) errors.push("GEMINI_API_KEY is missing");
    if (!config.githubToken) errors.push("GITHUB_TOKEN is missing");

    if (errors.length > 0) {
        throw new Error(`Configuration Error: ${errors.join(', ')}`);
    }
    return config;
}

import { GoogleGenerativeAI } from '@google/generative-ai';
import { delay as defaultDelay } from '../utils/helpers.js';

export class GeminiClient {
  constructor(apiKey, modelOrOptions = 'gemini-2.5-flash', delayFn = defaultDelay) {
    this.genAI = new GoogleGenerativeAI(apiKey);
    this.delay = delayFn;
    const options = typeof modelOrOptions === 'string'
      ? { primaryModel: modelOrOptions }
      : (modelOrOptions || {});

    this.primaryModel = options.primaryModel || 'gemini-2.5-flash';
    this.fallbackModels = (options.fallbackModels || []).filter(Boolean);
    this.retries = Number.isInteger(options.retries) && options.retries >= 0 ? options.retries : 3;
    this.models = new Map();
  }

  getModel(modelName) {
    if (!this.models.has(modelName)) {
      this.models.set(modelName, this.genAI.getGenerativeModel({ model: modelName }));
    }
    return this.models.get(modelName);
  }

  getCandidateModels(preferredModel) {
    const candidates = [preferredModel, this.primaryModel, ...this.fallbackModels].filter(Boolean);
    return [...new Set(candidates)];
  }

  async generateContent(prompt, retries = this.retries, options = {}) {
    let lastError;
    const candidateModels = this.getCandidateModels(options.preferredModel);

    for (let i = 0; i <= retries; i++) {
      for (const modelName of candidateModels) {
        try {
          const model = this.getModel(modelName);
          const result = await model.generateContent(prompt);
          const response = await result.response;
          return response.text();
        } catch (error) {
          lastError = error;
        }
      }

      if (i < retries) {
        const waitTime = Math.pow(2, i) * 2000; // Increased base from 1000 to 2000
        console.log(`Rate limited or error occurred. Waiting ${waitTime}ms before retry ${i + 1}...`);
        await this.delay(waitTime);
      }
    }

    throw lastError;
  }

  async generateJson(prompt, retries = this.retries, options = {}) {
      const jsonPrompt = `${prompt}

IMPORTANT: Return ONLY valid JSON.`;
      const text = await this.generateContent(jsonPrompt, retries, options);
      try {
          // Find JSON block if it exists
          const jsonMatch = text.match(/\{[\s\S]*\}|\[[\s\S]*\]/);
          const jsonStr = jsonMatch ? jsonMatch[0] : text;
          return JSON.parse(jsonStr);
      } catch (error) {
          throw new Error(`Failed to parse Gemini response as JSON: ${error.message}
Response: ${text}`);
      }
  }
}

import { Octokit } from '@octokit/rest';

export class GitHubClient {
  constructor(token, repoPath) {
    this.octokit = new Octokit({ auth: token });
    const [owner, repo] = repoPath.split('/');
    this.owner = owner;
    this.repo = repo;
  }

  async getIssue(issueNumber) {
    const { data } = await this.octokit.issues.get({
      owner: this.owner,
      repo: this.repo,
      issue_number: issueNumber
    });
    return data;
  }

  async createComment(issueNumber, body) {
    const { data } = await this.octokit.issues.createComment({
      owner: this.owner,
      repo: this.repo,
      issue_number: issueNumber,
      body
    });
    return data;
  }

  async addLabels(issueNumber, labels) {
    const { data } = await this.octokit.issues.addLabels({
      owner: this.owner,
      repo: this.repo,
      issue_number: issueNumber,
      labels
    });
    return data;
  }

  async getRef(ref) {
      const { data } = await this.octokit.git.getRef({
          owner: this.owner,
          repo: this.repo,
          ref: `heads/${ref}`
      });
      return data;
  }

  async createRef(ref, sha) {
      const { data } = await this.octokit.git.createRef({
          owner: this.owner,
          repo: this.repo,
          ref: `refs/heads/${ref}`,
          sha
      });
      return data;
  }

  async createCommit(message, tree, parents) {
      const { data } = await this.octokit.git.createCommit({
          owner: this.owner,
          repo: this.repo,
          message,
          tree,
          parents
      });
      return data;
  }

  async updateRef(ref, sha) {
      await this.octokit.git.updateRef({
          owner: this.owner,
          repo: this.repo,
          ref: `heads/${ref}`,
          sha
      });
  }

  async getDefaultBranch() {
    const { data } = await this.octokit.repos.get({
      owner: this.owner,
      repo: this.repo
    });
    return data.default_branch;
  }

  async createPullRequest(title, body, head, base) {
    const { data } = await this.octokit.pulls.create({
      owner: this.owner,
      repo: this.repo,
      title,
      body,
      head,
      base
    });
    return data;
  }

  async createIssue(title, body, labels = []) {
    const { data } = await this.octokit.issues.create({
      owner: this.owner,
      repo: this.repo,
      title,
      body,
      labels
    });
    return data;
  }

  async getLatestCommitSha(branch) {
      const targetBranch = branch || await this.getDefaultBranch();
      const { data } = await this.octokit.repos.getBranch({
          owner: this.owner,
          repo: this.repo,
          branch: targetBranch
      });
      return data.commit.sha;
  }

  async commitFile(filePath, content, message, branch) {
      const targetBranch = branch || await this.getDefaultBranch();
      const sha = await this.getLatestCommitSha(targetBranch);
      const { data: commit } = await this.octokit.git.getCommit({
          owner: this.owner,
          repo: this.repo,
          commit_sha: sha
      });

      const { data: blob } = await this.octokit.git.createBlob({
          owner: this.owner,
          repo: this.repo,
          content,
          encoding: 'utf-8'
      });

      const { data: tree } = await this.octokit.git.createTree({
          owner: this.owner,
          repo: this.repo,
          base_tree: commit.tree.sha,
          tree: [{
              path: filePath,
              mode: '100644',
              type: 'blob',
              sha: blob.sha
          }]
      });

      const newCommit = await this.createCommit(message, tree.sha, [sha]);
      await this.updateRef(targetBranch, newCommit.sha);
      return newCommit;
  }
}

#!/usr/bin/env node

import { Command } from 'commander';
import { readDb, writeDb } from './src/kanban/file-operations.js';
import { createTask, addTaskToColumn, moveTask, findTask } from './src/kanban/task-manager.js';
import { KANBAN_COLUMNS } from './src/utils/constants.js';

const program = new Command();

program
  .name('kanban')
  .description("Un outil CLI pour g√©rer un tableau Kanban pour une IA de code.")
  .version('1.0.0');

// Commande pour voir le tableau
program
  .command('voir')
  .description('Affiche le tableau Kanban complet.')
  .action(async () => {
    try {
      const db = await readDb();

      console.log('--- KANBAN ---');
      for (const [colonne, taches] of Object.entries(db)) {
          console.log(`\n[ ${colonne.toUpperCase()} ]`);
          if (taches.length === 0) {
              console.log('  (vide)');
          } else {
              taches.forEach(tache => {
                  console.log(`  - #${tache.id}: ${tache.titre}`);
              });
          }
      }
      console.log('\n--------------');
    } catch (error) {
      console.error("Erreur :", error.message);
      process.exit(1);
    }
  });

// Commande pour cr√©er une t√¢che
program
  .command('creer <titre>')
  .description("Cr√©e une nouvelle t√¢che dans la colonne 'id√©es'.")
  .action(async (titre) => {
    try {
      const db = await readDb();
      const task = createTask(titre);
      addTaskToColumn(db, task, KANBAN_COLUMNS.IDEES);
      await writeDb(db);
      console.log(`T√¢che #${task.id} "${titre}" cr√©√©e dans la colonne 'id√©es'.`);
    } catch (error) {
      console.error("Erreur :", error.message);
      process.exit(1);
    }
  });

// Commande pour bouger une t√¢che
program
  .command('bouger <id> <colonne>')
  .description('D√©place une t√¢che vers une nouvelle colonne.')
  .action(async (id, colonne) => {
    try {
      const db = await readDb();
      const taskId = parseInt(id);

      if (isNaN(taskId)) {
          throw new Error("L'ID de la t√¢che doit √™tre un nombre.");
      }

      // Valider la colonne cible
      const targetCol = colonne.toLowerCase();
      if (!Object.values(KANBAN_COLUMNS).includes(targetCol)) {
          throw new Error(`Colonne invalide : ${colonne}. Colonnes disponibles : ${Object.values(KANBAN_COLUMNS).join(', ')}`);
      }

      moveTask(db, taskId, targetCol);
      await writeDb(db);
      console.log(`T√¢che #${taskId} d√©plac√©e vers '${targetCol}'.`);
    } catch (error) {
      console.error("Erreur :", error.message);
      process.exit(1);
    }
  });

// Commande pour finir une t√¢che
program
  .command('finir <id>')
  .description("Marque une t√¢che comme termin√©e.")
  .action(async (id) => {
    try {
      const db = await readDb();
      const taskId = parseInt(id);

      if (isNaN(taskId)) {
          throw new Error("L'ID de la t√¢che doit √™tre un nombre.");
      }

      moveTask(db, taskId, KANBAN_COLUMNS.TERMINE);
      await writeDb(db);
      console.log(`T√¢che #${taskId} marqu√©e comme termin√©e.`);
    } catch (error) {
      console.error("Erreur :", error.message);
      process.exit(1);
    }
  });

program.parse(process.argv);

import fs from 'fs';
import { Octokit } from '@octokit/rest';

async function reportFailures() {
  const resultsPath = process.env.TEST_RESULTS_PATH || 'test-results.json';
  if (!fs.existsSync(resultsPath)) {
    console.log('No test results found.');
    return;
  }

  const results = JSON.parse(fs.readFileSync(resultsPath, 'utf8'));
  if (results.numFailedTests === 0) {
    console.log('No failed tests to report.');
    return;
  }

  const token = process.env.GITHUB_TOKEN;
  if (!token) {
    console.error('Error: GITHUB_TOKEN is not set.');
    process.exit(1);
  }

  const octokit = new Octokit({ auth: token });
  const [owner, repo] = process.env.GITHUB_REPOSITORY.split('/');

  for (const testFile of results.testResults) {
    for (const assertion of testFile.assertionResults) {
      if (assertion.status === 'failed') {
        const title = `Test Failure: ${assertion.fullName}`;
        const failureText = assertion.failureMessages.join('\n');
        const body = `
### Test Failure Details
- **File:** ${testFile.name}
- **Test:** ${assertion.fullName}
- **Status:** ${assertion.status}

### Error Message
\`\`\`
${failureText}
\`\`\`

---
*Reported by automated test workflow*
`;
        console.log(`Creating issue for: ${assertion.fullName}`);
        try {
          await octokit.issues.create({
            owner,
            repo,
            title,
            body,
            labels: ['bug', 'test-failure']
          });
        } catch (error) {
          console.error(`Failed to create issue for ${assertion.fullName}:`, error.message);
        }
      }
    }
  }
}

reportFailures().catch(err => {
  console.error(err);
  process.exit(1);
});

